//     res.write('<h3> Hello World! </h3>');
//     res.write('<h2> Chao mung den binh nguyen vo tan! </h2>');
//     res.end();
// })

// server.listen(3000, 'localhost', () =>{
//     console.log('Node.JS server is running on port 8080');
